import React, { useState } from "react";
import "./ExpenseForm.css"

const ExpenseForm=() => {
    // const [enteredtitle,setenteredtitle]=useState('');
    // const [entereddate,setentereddate]=useState('');
    // const [enteredamount,setenteredamount]=useState('');
    const [userInput,setUSerInput]=useState({
        enteredtitle:'',
        entereddate:'',
        enteredamount:''
    }) 
     
    const titlechange=(evt)=>{
        console.log(evt.target.value);
        // setenteredtitle(evt.target.value);
        setUSerInput({
            ...userInput,
            enteredtitle:evt.target.value
        })
    }
    
    const datechange=(evt)=>{
        console.log(evt.target.value);
        // setentereddate(evt.target.value);
        setUSerInput({
            ...userInput,
            entereddate:evt.target.value
        })
    }
    const amountchange=(evt)=>{
        console.log(evt.target.value);
        //setenteredamount(evt.target.value);
        setUSerInput({
            ...userInput,
            enteredamount:evt.target.value
        })   
    }
    return (
        <form>
            <div className="new-expense_controls" >
                <label htmlFor="title">title</label>
                <input type="text" id="title" placeholder="Enter the title" onInput={titlechange}/>
            </div>
            
            <div className="new-expense_controls">
                <label htmlFor="date">date</label>
                <input type="date" id="date" placeholder="select a date" onInput={datechange} />
            </div>
            
            <div className="new-expense_controls">
                <label htmlFor="amount">Amount</label>
                <input type="number" id="amount" placeholder="Enter the amount" onInput={amountchange}/>
            </div>
            <div className=".new-expense__actions">
                <button>update</button>
            </div>
        
        </form>
    )
}

export default ExpenseForm;
